mapper.select:

where d.del_flag = '0'

    AND parent_id = #{parentId}


    AND category_name like concat('%', #{categoryName}, '%')


    AND status = #{status}


${params.dataScope}
order by d.parent_id, d.order_num

select count(1) from sys_category
where del_flag = '0'
and category_id = #{categoryId}
and parent_id = #{parentId}

mapper.update:
update sys_category
parent_id = #{parentId}, category_name = #{categoryName}, ancestors = #{ancestors}, order_num = #{orderNum}, status = #{status}, update_by = #{updateBy}, update_time = sysdate()
where category_id = #{categoryId}

update sys_category set ancestors =

    when #{item.categoryId} then #{item.ancestors}

where category_id in

    #{item.categoryId}
